import React from 'react';
import { useNavigate } from 'react-router-dom';
import { deleteContact } from '../api/ContactService';  // à adapter selon ton chemin
import { toast } from 'react-toastify';

const Contact = ({ contact, getAllContacts, currentPage }) => {
  const navigate = useNavigate();

  const {
    id, photoUrl, name, title, email, address, phone, status
  } = contact;

  const handleDelete = async () => {
    if (!window.confirm("Voulez-vous vraiment supprimer ce contact ?")) return;
    try {
      await deleteContact(id);
      toast.success("Contact supprimé !");
      getAllContacts(currentPage);  // Recharge la liste après suppression
    } catch (error) {
      toast.error("Erreur lors de la suppression !");
    }
  };

  return (
    <div className="contact_item">
      <div className="contact_header">
        <div className="contact_image">
          <img src={photoUrl || '/default.png'} alt={name || 'Unnamed'} />
        </div>
        <div className="contact_details">
          <p>{name}</p>
          <p>{title}</p>
        </div>
      </div>

      <div className="contact_body">
        <p>{email}</p>
        <p>{address}</p>
        <p>{phone}</p>
        <p>{status}</p>
      </div>

      <div className="contact_footer">
        <button onClick={() => navigate(`/contacts/${id}`)}>Voir détails</button>
        <button onClick={handleDelete}>Supprimer</button>
      </div>
    </div>
  );
};

export default Contact;
